import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const highlightArray = [(<p>Coding</p>), (<p>Product Management</p>), (<p>IITK & IIMA</p>), (<p>Coding</p>), (<p>Product Management</p>), (<p>IITK & IIMA</p>)];


export default function Highlights() {
  const [activeDiv, setActiveDiv] = useState(1);

useEffect(()=>{
  document.getElementById(activeDiv).classList.remove('inactive');
  document.getElementById(activeDiv + 1).classList.remove('inactive');
  document.getElementById(activeDiv - 1).classList.remove('inactive');
  document.getElementById(activeDiv + 1).classList.add('next');
  document.getElementById(activeDiv - 1).classList.add('prev');
},[activeDiv])

  const handleRight = () => {
    setActiveDiv(prev => prev + 1);
    /*const oldNextDiv = document.getElementById(activeDiv);
    const oldMainDiv = document.getElementById(activeDiv - 1);
    const oldPrevDiv = document.getElementById(activeDiv - 2);
    const newNextDiv = document.getElementById(activeDiv + 1);
    oldNextDiv.classList.remove('next');
    newNextDiv.classList.add('next');
    newNextDiv.classList.remove ('inactive');
    oldMainDiv.classList.add('prev');
    oldPrevDiv.classList.remove('prev');
    oldPrevDiv.classList.add('inactive');*/
  }


  return (
    <div className="highlight-container">
      {highlightArray.map((content, index) => (<div id={index} className='highlight-tile inactive'>{content}</div>))}
      <button onClick={handleRight}>Next</button>
    </div>
  );


}
