import React, { useEffect, useState } from 'react';
import { useDispatch } from 'react-redux';
import { selectAllData, editMonthlyAcProd, editPage } from '../../Store/inputDataSlice';
import { useSelector } from 'react-redux';
import { keyPvWatts } from '../Components/APIkeys';
import EditBills from '../Components/editBills';
import EditArray from '../Components/editArray';

async function callPvWatts(url) {
  const response = await fetch(url);
  const data = await response.json();
  return data;
}

export default function Result(props) {
  const dispatch = useDispatch();
  const allData = useSelector(selectAllData);
  const monthly_ac_prod = allData.monthly_ac_prod;
  const tariff = allData.userInputs.tariff;
  const monthlyBills = allData.userInputs.bills;
  const monthlyCons = monthlyBills.map(bill => bill / tariff);
  const yearlyCons = monthlyCons.reduce((ac, cv) => ac + cv);
  const yearlyProd = (monthly_ac_prod.length !== 0) ? monthly_ac_prod.reduce((ac, cv) => ac + cv) : 0;
  const yearlyDiff = yearlyCons - yearlyProd;
  let oldBill = monthlyBills.reduce((ac, cv) => ac + cv);
  let newBill = 0;
  if (yearlyDiff > 0) {
    newBill = yearlyDiff * tariff;
  }
  let yearlySavings = Math.round(oldBill - newBill);


  const userAddress = props.address;
  const [isLoading, setIsLoading] = useState(false);
  const [showBills, setShowBills]= useState(false);
  const [showArray, setshowArray] = useState(false);

  useEffect(() => {
    const getMonthlyProd = async (url) => {
      setIsLoading(true);
      const response = await callPvWatts(url);
      const acOutputs = response.outputs.ac_monthly;
      dispatch(editMonthlyAcProd(acOutputs));
      setIsLoading(false);
    }
    let pvWattsURL = `https://developer.nrel.gov/api/pvwatts/v8.json?api_key=${keyPvWatts}&azimuth=180&system_capacity=4&losses=14&array_type=1&module_type=0&gcr=0.4&dc_ac_ratio=1.2&inv_eff=96.0&radius=0&dataset=nsrdb&tilt=10&address=${userAddress}&soiling=12|4|45|23|9|99|67|12.54|54|9|0|7.6&albedo=0.3&bifaciality=0.7`;
    getMonthlyProd(pvWattsURL);
  }, [dispatch, userAddress])


  const handleClick = () => {
    dispatch(editPage(1));
  }

  return (
    <div className="result">
      {isLoading ? <p className='white-text'>Loading...</p> : (
        <>
          <p className='white-text'>Estimated current bill per year: £ {Math.round(oldBill)}</p>
          <p className='white-text'>Estimated solar production per year: {Math.round(yearlyProd)} kWh</p>
          <p className='white-text'>Estimated new bill per year: £ {Math.round(newBill)}</p>
          <p id='page-2-line-1' className='white-text'>You can save £ {yearlySavings} per year if you install solar</p>
          <div className='actions-div'>
            <button onClick={() => setShowBills(prev=>!prev)}>Edit bills</button>
            <p className='white-text'>Update your current bills to get a more accurate estimate</p>
            {showBills && <EditBills />}
            <button onClick={() => setShowArray(prev=>!prev)}>Edit Roof Area</button>
            <p className='white-text'>Update your roof area to get a more accurate estimate</p>
            {showArray && <EditArray />}
            <button onClick={handleClick}>Go Back</button>
          </div>
        </>
      )}
    </div>
  )
}
