import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const array = [(
  <div className='about-me-tiles'>
    <p className='tile-name'>Summary</p>
    <p><em>I'm a budding developer passionate about coding and creating meaningful projects. I have worked as a product manager for last 6 years, and am now venturing into the world of programming.</em></p>
  </div>
), (
  <div className='about-me-tiles'>
    <p className='tile-name'>Skills</p>
    {/*<img src='./html_css_js.png' alt='skills' className='logo' />*/}
    <ul id='skill-list'>
      <li>HTML | CSS | ReactJS</li>
      <li>JavaScript | Python</li>
      <li>Redux | ExpressJS | Github</li>
    </ul>
  </div>
), (
  <div className='about-me-tiles'>
    <p className='tile-name'>Qualifications</p>
    <ul id='quals'>
      <li>B.Tech. from IIT Kanpur</li>
      <li>Front-end developer certification from CodeAcademy</li>
      <li>MBA from IIM Ahmedabad</li>
    </ul>
  </div>
)];

const highlightArray = [...array, ...array, ...array, ...array, ...array, ...array, ...array, ...array, ...array];


export default function Highlights() {
  const [activeDiv, setActiveDiv] = useState(3);

  useEffect(() => {
    document.getElementById(activeDiv).classList.remove('inactive');
    document.getElementById(activeDiv + 1).classList.remove('inactive');
    document.getElementById(activeDiv - 1).classList.remove('inactive');
    document.getElementById(activeDiv + 1).classList.add('next');
    document.getElementById(activeDiv - 1).classList.add('prev');
  })
  // 1>2>3
  //
  const handleRight = () => {
    setActiveDiv(prev => prev + 1);
    const oldNextDiv = document.getElementById(activeDiv + 1);
    const oldMainDiv = document.getElementById(activeDiv);
    const oldPrevDiv = document.getElementById(activeDiv - 1);
    const newNextDiv = document.getElementById(activeDiv + 2);
    oldNextDiv.classList.remove('next');
    newNextDiv.classList.add('next');
    newNextDiv.classList.remove('inactive');
    oldMainDiv.classList.add('prev');
    oldPrevDiv.classList.remove('prev');
    oldPrevDiv.classList.add('inactive');
  }

  const handleLeft = () => {
    setActiveDiv(prev => prev - 1);
    const oldNextDiv = document.getElementById(activeDiv - 1);
    const oldMainDiv = document.getElementById(activeDiv);
    const oldPrevDiv = document.getElementById(activeDiv + 1);
    const newNextDiv = document.getElementById(activeDiv - 2);
    //console.log(oldPrevDiv, oldMainDiv, oldNextDiv, newNextDiv);
    oldNextDiv.classList.remove('next');
    newNextDiv.classList.add('next');
    newNextDiv.classList.remove('inactive');
    oldMainDiv.classList.add('prev');
    oldPrevDiv.classList.remove('prev');
    oldPrevDiv.classList.add('inactive');
  }


  return (
    <div>
      <div className="highlight-container">
        {highlightArray.map((content, index) => (<div id={index} className='highlight-tile inactive'>{content}</div>))}
      </div>
      <div className='buttons-div'>
        <button onClick={handleLeft} className='right-button'><i class="fa-solid fa-arrow-left"></i></button>
        <button onClick={handleRight} className='right-button'><i class="fa-solid fa-arrow-right"></i></button>
      </div>
    </div>
  );


}
