import React, { useRef, useEffect } from 'react';

const highlightArray = [(<p>Coding</p>),(<p>Product Management</p>),<p>IITK & IIMA</p>];


export default function Highlights() {

  const containerRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      if (containerRef.current) {
        containerRef.current.scrollLeft += 2; // Adjust the scroll speed as needed
      }
    }, 50); // Adjust the interval duration as needed

    return () => clearInterval(interval); // Clean up the interval on component unmount
  }, []); // Run only once on component mount

  return (
    <div className="highlight-container" ref={containerRef}>
      {highlightArray.map((content,index)=>(<div id={index} className='highlight-tile'>{content}</div>))}
    </div>
  );


}
