import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';

const array = [(
  <p>I'm a budding developer passionate about coding and creating meaningful projects. I have worked as a product manager for last 6 years, and am now venturing into the world of programming. My Bachelors (B.Tech.) and Masters(MBA) degrees are from IIT Kanpur and IIM Ahmedabad respectively.</p>
), (
  <p>Skills:</p>

), (
  <p>IITK & IIMA</p>
)];

const highlightArray = [...array, ...array, ...array, ...array];


export default function Highlights() {
  const [activeDiv, setActiveDiv] = useState(2);

  useEffect(() => {
    document.getElementById(activeDiv).classList.remove('inactive');
    document.getElementById(activeDiv + 1).classList.remove('inactive');
    document.getElementById(activeDiv - 1).classList.remove('inactive');
    document.getElementById(activeDiv + 1).classList.add('next');
    document.getElementById(activeDiv - 1).classList.add('prev');
  }, [])
  // 1>2>3
  //
  const handleRight = () => {
    setActiveDiv(prev => prev + 1);
    const oldNextDiv = document.getElementById(activeDiv + 1);
    const oldMainDiv = document.getElementById(activeDiv);
    const oldPrevDiv = document.getElementById(activeDiv - 1);
    const newNextDiv = document.getElementById(activeDiv + 2);
    console.log(oldPrevDiv, oldMainDiv, oldNextDiv, newNextDiv);
    oldNextDiv.classList.remove('next');
    newNextDiv.classList.add('next');
    newNextDiv.classList.remove('inactive');
    oldMainDiv.classList.add('prev');
    oldPrevDiv.classList.remove('prev');
    oldPrevDiv.classList.add('inactive');
  }


  return (
    <div>
      <div className="highlight-container">
        {highlightArray.map((content, index) => (<div id={index} className='highlight-tile inactive'>{content}</div>))}
      </div>
      <button onClick={handleRight} className='right-button'>Next</button>
    </div>
  );


}
