import React, { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { editMonthlyAcProd, editPage, inputInfo } from '../../Store/inputDataSlice';
import { useSelector } from 'react-redux';

export default function Result() {

  const dispatch = useDispatch();
  let userAddress = useSelector(inputInfo);
  let pvWattsURL = `https://developer.nrel.gov/api/pvwatts/v8.json?api_key=DEMO_KEY&azimuth=180&system_capacity=4&losses=14&array_type=1&module_type=0&gcr=0.4&dc_ac_ratio=1.2&inv_eff=96.0&radius=0&dataset=nsrdb&tilt=10&address=${userAddress},%20co&soiling=12|4|45|23|9|99|67|12.54|54|9|0|7.6&albedo=0.3&bifaciality=0.7`
  async function callPvWatts(url) {
    const response = await fetch(url);
    const data = response.json();
    return data;
  }

  useEffect(()=>{
    const getMonthlyProd = async (url)=>{
      const response = await callPvWatts(url);
      const acOutputs = response.outputs.ac_monthly;
      console.log(acOutputs);
      dispatch(editMonthlyAcProd(acOutputs));
    }

    getMonthlyProd(pvWattsURL);

  })


  const handleClick = () => {
    dispatch(editPage(1));
  }

  return (
    <div className="result">
      <p id='page-2-line-1' className='white-text'>You can save $100 if you install solar</p>
      <button onClick={handleClick}>Go Back</button>
      <br />
    </div>
  )
}
