export default function polygonArea(coords) {
  let area = 0;
  let j = coords.length - 1;

  for (let i = 0; i < coords.length; i++) {
      area += (coords[j].x + coords[i].x) * (coords[j].y - coords[i].y);
      j = i;
  }

  return Math.abs(area / 2);
}
