import React, { useState } from "react";
import {useSelector} from 'react-redux';
import { inputInfo} from "../../Store/inputDataSlice";

export default function EditBills(){
const bills = useSelector(inputInfo).bills;




  return (
    <div className="popup bills-popup">
      {bills.map((bill,ind)=>(
      <div className="bill-sliders">
        <label className="month white-text">{ind}</label>
      <input type='range' min="10" max="400" step="1"  value={bill} />
      <span className="bill-value white-text">{bill}</span>
      </div>
      ))}

    </div>
  )
}
