import React, { useRef, useEffect } from 'react';
import { useDispatch } from "react-redux";
import { editAddress, editPage } from "../bestofreddit/App/inputData";
import store from '../bestofreddit/App/store';

export default function Introduction(props) {
  const autocompleteInputRef = useRef(null);

  useEffect(() => {
    const autocomplete = new window.google.maps.places.Autocomplete(
      autocompleteInputRef.current, {
        componentRestrictions: { country: 'UK' }
      }
    );
    // Listen for the 'place_changed' event
    autocomplete.addListener('place_changed', () => {
      const place = autocomplete.getPlace();
      console.log(place); // You can handle the selected place here
    });
  }, []);

  const dispatch = useDispatch();
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(editAddress(e.target.elements.address.value));
    dispatch(editPage(2));
    console.log(store.getState());
  }

  return (
    <div id="introduction">
      <div id='intro-1'></div>
      <h1 id='intro-text'>Should I install solar on my house?</h1>
      <br />
      <form onSubmit={handleSubmit}>
        <input type="text" id="address-input" placeholder="Enter your address" name="address" ref={autocompleteInputRef}></input>
        <br/>
        <br/>
        <button type="submit" id='submit-button' >CHECK</button>
      </form>
    </div>
  )
}
