import { createSlice } from "@reduxjs/toolkit";


const initialState = {
  userInputs: {
    address: '',
    name: '',
    bills: {
      jan: 210,
      feb: 200,
      mar: 180,
      apr: 120,
      may: 70,
      jun: 65,
      jul: 68,
      aug: 67,
      sep: 72,
      oct: 95,
      nov: 136,
      dec: 195
    },
    tariff: 0.27,
  },
  page: 1
};

const options = {
  name: 'inputData',
  initialState: initialState,
  reducers: {
    editDataInput: (state, action) => {
      return action.payload;
    },
    editAddress: (state, action) => {
      state.address = action.payload;
      return state;
    },
    editName: (state, action) => {
      state.name = action.payload;
      return state;
    },
    editTariff: (state, action) => {
      state.tariff = action.payload;
      return state;
    },
    editBills: (state, action) => {
      state.address = action.payload;
      return state;
    },
    editPage: (state, action) => {
      state.page = action.payload;
      return state;
    }
  }
};

const inputDataSlice = createSlice(options);

export const pageInfo = (state)= state.inputData.page;
export const { editInputData, editAddress, editBills, editName, editTariff, editPage } = inputDataSlice.actions;
export default inputDataSlice.reducer;
