import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

const bfMeals =['Poha','Upma','Oats','Besan Chila','Sandwich','Pancakes'];
const lunMeals =['Puloa','Fried Rice','Noodles','Pasta','Quinoa'];
const dinMeals=['Puloa','Fried Rice','Noodles','Pasta','Quinoa','Burger'];


function App() {
  const [bfSugg,setBfSugg]=useState([]);
  const [lunSugg,setLunSugg]=useState([]);
  const [dinSugg,setDinSugg]=useState([]);

  function randomMeals(){
    let output=[];
    for (let i=0;i<5;i++){
      const newrand=Math.floor(Math.random()*bfMeals.length);
      if (!output.includes(newrand)){
        output.push(newrand);
      }
      else i--;
    }
    setBfSugg(output.map(key=>bfMeals[key]));
  }

  return (
    <div className="App">
      <header className="App-header">
        <p>Get 5 random suggestions for each meal of the day</p>
      </header>
      <main>
      <button onClick={randomMeals}>Get Meal Suggestions</button>
        {bfSugg.length!==0 && <MealDiv mealSugg={bfSugg}/>}
        {lunSugg.length!==0 && <MealDiv mealSugg={lunSugg}/>}
        {dinSugg.length!==0 && <MealDiv mealSugg={dinSugg}/>}

    </main>
    </div>
  );
}

function MealDiv(props) {
return (
  <div>
    <ul>
      {props.mealSugg.map(key=> (<li key={key}>{key}</li>))}
    </ul>
  </div>
);
}

export default App;
