import React, {useState} from "react";
import { NavLink } from "react-router-dom";

export default function Navigate(){
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

return (
  <nav className="navbar">
    <div className="navbar-header">
      <button className="menu-toggle" aria-label="Toggle Menu" onClick={toggleMenu}>â˜°</button>
    </div>
    <ul className= {`navbar-menu ${(isOpen)?'active':""}`}>
      <li><NavLink className='nav-link' to="/">About Me</NavLink></li>
      <li><NavLink className='nav-link' to="/meal-suggestor">Meal Suggestor</NavLink></li>
      <li><NavLink className='nav-link' to="/playlist-creator">Playlist Creator</NavLink></li>
    </ul>
    <div className="nav-divider"></div>
  </nav>
)
}
